        border-radius: 2px;
    }
    .panel > .header {
        padding: 16px;
    }
    .dropdown-menu {
        border: none;
        border-radius: 2px !important;
        padding-top: 8px;
        padding-bottom: 8px;
        display: block;
        opacity: 0;
        transition: opacity 0.2s;
        visibility: hidden;
    }
    li.open > .dropdown-menu {
       opacity: 1;
       visibility: visible;
    }
    .dropdown-menu > li > a {
        padding-top: 10px;
        padding-bottom: 10px;
        padding-left: 16px;
        padding-right: 16px;
        font-size: 16px;
    }
    .dropdown-menu > li.disabled > a {
        text-decoration: none !important;
    }
